// Design click function

$(document).ready(function() {
    $(".designImg").click(function() {
      $("#design").toggle();
    });
});

$(document).ready(function(){
    $(".devImg").click(function(){
      $("#dev").toogle();
    });
});

$(document).ready(function(){
    $(".manImg").click(function(){
      $("#product-m").toogle();
    });
});


